const rentals = queries.query_active_rentals.data || [];
const waitlist = queries.query_user_waitlist.data || [];

return data.map(book => {
  const isRented = rentals.some(r => r.book_id === book.book_id);
  const isWaitlisted = waitlist.some(w => w.book_id === book.book_id);

  let status = "Available";
  if (isRented) status = "Checked Out";
  else if (isWaitlisted) status = "Waitlisted";

  return {
    ...book,
    status
  };
});

