const bookId = components.book_catalog_table.selectedRow.book_id;
const waitlist = queries.query_user_waitlist.data.filter(w => w.book_id === bookId);
const copies = queries.query_all_book_copies.data.filter(c => c.book_id === bookId);
const rentals = queries.query_active_rentals_by_copy.data;

const rentedCopyIds = rentals.map(r => r.copy_id);
const availableCopy = copies.find(c => !rentedCopyIds.includes(c.copy_id));

if (!waitlist.length || !availableCopy) {
 return { message: "No waitlisted users or available copies." };
}

const nextUser = waitlist.sort((a, b) => a.position - b.position)[0];

await queries.query_checkout_book.run({
 copy_id: availableCopy.copy_id,
 book_id: bookId,
 user_id: nextUser.user_id,
 checkout_date: moment().format('YYYY-MM-DD'),
 due_date: moment().add(14, 'days').format('YYYY-MM-DD'),
 return_date: null
});

await queries.query_remove_from_waitlist.run({
 waitlist_id: nextUser.waitlist_id
});

return { message: `Assigned copy ${availableCopy.copy_id} to ${nextUser.user_id}` };

